import { useEffect, useState } from "react";
import data from "../data.json";

function Navbar({ setPlanet, activePlanet }) {
  const [planetNames, setPlanetNames] = useState([]);

  useEffect(() => {
    setPlanetNames(data.map((planet) => planet.name));
  }, []);
  return (
    <div className="navbar">
      <div className="logo">the planets</div>
      <nav>
        <ul>
          {planetNames.map((planet, index) => (
            <li
              className={
                activePlanet === planet.toLowerCase()
                  ? planet.toLowerCase()
                  : ""
              }
              onClick={() => setPlanet(planet)}
              key={index}
            >
              {planet}
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

export default Navbar;
