import { useEffect, useState } from "react";
import Navbar from "./components/Navbar";

import data from "./data.json";

function App() {
  const [planet, setPlanet] = useState("mercury");
  const [planetDetail, setPlanetDetail] = useState(null);

  useEffect(() => {
    const foundPlanet = data.find(
      (p) => p.name.toLowerCase() === planet.toLowerCase(),
    );
    setPlanetDetail(foundPlanet);
  }, [planet]);

  if (!planetDetail) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <Navbar />
      <div className="content">
        <div className="left-side">
          <img
            src={planetDetail.images.planet}
            alt={planet}
            className="planet-image"
          />
        </div>
        <div className="right-side">
          <h1>{planetDetail.name}</h1>
          <p>{planetDetail.overview.content}</p>
          <p>
            Source: <a href={planetDetail.overview.source}>Wikipedia</a>{" "}
          </p>
          <div className="buttons-div">
            <button>
              <span>01</span>overview
            </button>
            <button>
              <span>02</span>internal structure
            </button>
            <button>
              <span>03</span>surface geology
            </button>
          </div>
        </div>
      </div>
      <div className="content-footer">
        <div className="footer-item">
          <p>rotating time</p>
          <h2>{planetDetail.rotating}</h2>
        </div>
        <div className="footer-item">
          <p>revolution time</p>
          <h2>{planetDetail.revolution}</h2>
        </div>
        <div className="footer-item">
          <p>revolution radius</p>
          <h2>{planetDetail.radius}</h2>
        </div>
        <div className="footer-item">
          <p>average temp.</p>
          <h2>{planetDetail.temperature}</h2>
        </div>
      </div>
    </div>
  );
}

export default App;
