import { useEffect, useState } from "react";
import data from "../data.json";

function Navbar({ setPlanet }) {
  const [planetNames, setPlanetNames] = useState([]);
  const [hoveredPlanet, setHoveredPlanet] = useState();

  useEffect(() => {
    setPlanetNames(data.map((planet) => planet.name));
  }, []);
  return (
    <div className="navbar">
      <div className="logo">the planets</div>
      <nav>
        <ul>
          {planetNames.map((planet, index) => (
            <li onClick={() => setPlanet(planet)} key={index}>
              {planet}
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

export default Navbar;
