import { useEffect, useState } from "react";
import data from "../data.json";

function Navbar({ setPlanet, setIsMenuOpen }) {
  const [planetNames, setPlanetNames] = useState([]);
  const [hoveredPlanet, setHoveredPlanet] = useState(null);

  useEffect(() => {
    setPlanetNames(data.map((planet) => planet.name));
  }, []);

  const handleMouseEnter = (planet) => {
    setHoveredPlanet(planet);
  };

  const handleMouseLeave = () => {
    setHoveredPlanet(null);
  };

  return (
    <div className="navbar">
      <div className="logo">the planets</div>
      <nav className="mobile">
        <ul>
          {planetNames.map((planet, index) => (
            <li
              onClick={() => setPlanet(planet)}
              key={index}
              onMouseEnter={() => handleMouseEnter(planet)}
              onMouseLeave={handleMouseLeave}
              className={planet === hoveredPlanet ? hoveredPlanet : ""}
            >
              {planet}
            </li>
          ))}
        </ul>
      </nav>
      <svg
        className="icon-menu"
        xmlns="http://www.w3.org/2000/svg"
        width="24"
        height="17"
      >
        <g fill="#FFF" fill-rule="evenodd">
          <path d="M0 0h24v3H0zM0 7h24v3H0zM0 14h24v3H0z" />
        </g>
      </svg>
    </div>
  );
}

export default Navbar;
