import { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import Buttons from "./components/Buttons";
import data from "./data.json";

function App() {
  const [planet, setPlanet] = useState("mercury");
  const [planetDetail, setPlanetDetail] = useState(null);

  useEffect(() => {
    const foundPlanet = data.find(
      (p) => p.name.toLowerCase() === planet.toLowerCase(),
    );
    setPlanetDetail(foundPlanet);
  }, [planet]);

  if (!planetDetail) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <Navbar />
      <div className="content">
        <div className="left-side"></div>
        <div className="right-side">
          <h1>{planetDetail.name}</h1>
          <p>{planetDetail.overview.content}</p>
          <p>
            Source: <a href={planetDetail.overview.source}>Wikipedia</a>{" "}
          </p>
          <Buttons />
        </div>
      </div>
    </div>
  );
}

export default App;
